contracts/CreatorToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title CreatorToken
 * @dev ERC20 token for creator rewards and engagement
 * Supports minting for engagement rewards and burning for redemption
 */
contract CreatorToken is ERC20, ERC20Burnable, Ownable, ReentrancyGuard {
    // Events
    event TokensMinted(address indexed to, uint256 amount, string reason);
    event TokensBurned(address indexed from, uint256 amount, string reason);

    // Engagement reward types
    enum RewardType {
        LIKE,
        COMMENT,
        SHARE,
        REMIX,
        TIP
    }

    // Mapping to track engagement rewards per user per content
    mapping(address => mapping(bytes32 => mapping(RewardType => uint256))) public engagementRewards;
    mapping(address => mapping(bytes32 => bool)) public hasClaimedReward;

    // Platform fee for token operations (in basis points, 100 = 1%)
    uint256 public platformFee = 500; // 5%

    // Maximum supply
    uint256 public constant MAX_SUPPLY = 1_000_000_000 * 10**18; // 1 billion tokens

    constructor(
        string memory name,
        string memory symbol,
        address initialOwner
    ) ERC20(name, symbol) Ownable(initialOwner) {
        _transferOwnership(initialOwner);
    }

    /**
     * @dev Mint tokens for engagement rewards
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint
     * @param contentId Content identifier
     * @param rewardType Type of engagement reward
     */
    function mintEngagementReward(
        address to,
        uint256 amount,
        bytes32 contentId,
        RewardType rewardType
    ) external onlyOwner nonReentrant {
        require(totalSupply() + amount <= MAX_SUPPLY, "CreatorToken: Max supply exceeded");
        require(amount > 0, "CreatorToken: Amount must be positive");

        // Check if user already claimed this reward type for this content
        require(
            !hasClaimedReward[to][contentId] ||
            engagementRewards[to][contentId][rewardType] == 0,
            "CreatorToken: Reward already claimed"
        );

        // Calculate platform fee
        uint256 fee = (amount * platformFee) / 10000;
        uint256 netAmount = amount - fee;

        // Mint tokens
        _mint(to, netAmount);
        _mint(owner(), fee); // Platform fee goes to owner

        // Track reward
        engagementRewards[to][contentId][rewardType] = netAmount;
        hasClaimedReward[to][contentId] = true;

        emit TokensMinted(to, netAmount, "engagement_reward");
    }

    /**
     * @dev Mint tokens for content creation rewards
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint
     */
    function mintContentReward(address to, uint256 amount) external onlyOwner nonReentrant {
        require(totalSupply() + amount <= MAX_SUPPLY, "CreatorToken: Max supply exceeded");
        require(amount > 0, "CreatorToken: Amount must be positive");

        _mint(to, amount);
        emit TokensMinted(to, amount, "content_reward");
    }

    /**
     * @dev Burn tokens for redemption
     * @param amount Amount of tokens to burn
     */
    function redeemTokens(uint256 amount) external nonReentrant {
        require(amount > 0, "CreatorToken: Amount must be positive");
        require(balanceOf(msg.sender) >= amount, "CreatorToken: Insufficient balance");

        _burn(msg.sender, amount);
        emit TokensBurned(msg.sender, amount, "redemption");
    }

    /**
     * @dev Set platform fee (only owner)
     * @param newFee New platform fee in basis points
     */
    function setPlatformFee(uint256 newFee) external onlyOwner {
        require(newFee <= 1000, "CreatorToken: Fee too high"); // Max 10%
        platformFee = newFee;
    }

    /**
     * @dev Get engagement reward amount for a user and content
     * @param user User address
     * @param contentId Content identifier
     * @param rewardType Type of reward
     */
    function getEngagementReward(
        address user,
        bytes32 contentId,
        RewardType rewardType
    ) external view returns (uint256) {
        return engagementRewards[user][contentId][rewardType];
    }

    /**
     * @dev Check if user has claimed reward for content
     * @param user User address
     * @param contentId Content identifier
     */
    function hasUserClaimedReward(address user, bytes32 contentId) external view returns (bool) {
        return hasClaimedReward[user][contentId];
    }
}

contracts/EngagementReward.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./CreatorToken.sol";

/**
 * @title EngagementReward
 * @dev Contract for managing engagement-based rewards on content
 * Handles likes, comments, shares, and remixes with token rewards
 */
contract EngagementReward is Ownable, ReentrancyGuard {
    // Reference to the CreatorToken contract
    CreatorToken public creatorToken;

    // Events
    event EngagementRewarded(
        address indexed user,
        bytes32 indexed contentId,
        CreatorToken.RewardType rewardType,
        uint256 amount
    );

    event RewardRateUpdated(CreatorToken.RewardType rewardType, uint256 newRate);

    // Reward rates for different engagement types (in token amount)
    mapping(CreatorToken.RewardType => uint256) public rewardRates;

    // Mapping to track engagement counts per content
    mapping(bytes32 => mapping(CreatorToken.RewardType => uint256)) public engagementCounts;

    // Mapping to track if user has engaged with content
    mapping(address => mapping(bytes32 => mapping(CreatorToken.RewardType => bool))) public hasEngaged;

    // Maximum engagements per user per content per type (to prevent abuse)
    uint256 public constant MAX_ENGAGEMENTS_PER_USER = 1;

    // Cooldown period between engagements (in seconds)
    uint256 public engagementCooldown = 3600; // 1 hour

    // Mapping to track last engagement time
    mapping(address => mapping(bytes32 => uint256)) public lastEngagementTime;

    constructor(address _creatorToken) Ownable(msg.sender) {
        require(_creatorToken != address(0), "EngagementReward: Invalid token address");
        creatorToken = CreatorToken(_creatorToken);

        // Set default reward rates
        rewardRates[CreatorToken.RewardType.LIKE] = 10 * 10**18;      // 10 tokens
        rewardRates[CreatorToken.RewardType.COMMENT] = 25 * 10**18;   // 25 tokens
        rewardRates[CreatorToken.RewardType.SHARE] = 50 * 10**18;     // 50 tokens
        rewardRates[CreatorToken.RewardType.REMIX] = 100 * 10**18;    // 100 tokens
        rewardRates[CreatorToken.RewardType.TIP] = 0;                 // Tips are variable
    }

    /**
     * @dev Reward user for engagement
     * @param user Address of the user to reward
     * @param contentId Content identifier
     * @param rewardType Type of engagement
     */
    function rewardEngagement(
        address user,
        bytes32 contentId,
        CreatorToken.RewardType rewardType
    ) external onlyOwner nonReentrant {
        require(user != address(0), "EngagementReward: Invalid user address");
        require(contentId != bytes32(0), "EngagementReward: Invalid content ID");

        // Check cooldown period
        require(
            block.timestamp >= lastEngagementTime[user][contentId] + engagementCooldown,
            "EngagementReward: Cooldown period not passed"
        );

        // Check if user hasn't already engaged with this type
        require(
            !hasEngaged[user][contentId][rewardType],
            "EngagementReward: User already engaged"
        );

        uint256 rewardAmount = rewardRates[rewardType];
        require(rewardAmount > 0, "EngagementReward: No reward for this type");

        // Mint reward tokens
        creatorToken.mintEngagementReward(user, rewardAmount, contentId, rewardType);

        // Update tracking
        hasEngaged[user][contentId][rewardType] = true;
        engagementCounts[contentId][rewardType]++;
        lastEngagementTime[user][contentId] = block.timestamp;

        emit EngagementRewarded(user, contentId, rewardType, rewardAmount);
    }

    /**
     * @dev Reward tip to content creator
     * @param from Address sending the tip
     * @param to Address receiving the tip
     * @param contentId Content identifier
     * @param amount Amount of tokens to tip
     */
    function rewardTip(
        address from,
        address to,
        bytes32 contentId,
        uint256 amount
    ) external onlyOwner nonReentrant {
        require(from != address(0) && to != address(0), "EngagementReward: Invalid addresses");
        require(contentId != bytes32(0), "EngagementReward: Invalid content ID");
        require(amount > 0, "EngagementReward: Invalid amount");

        // Transfer tokens from sender to receiver
        // Note: This assumes the contract has approval to transfer tokens
        creatorToken.transferFrom(from, to, amount);

        emit EngagementRewarded(to, contentId, CreatorToken.RewardType.TIP, amount);
    }

    /**
     * @dev Update reward rate for engagement type
     * @param rewardType Type of engagement
     * @param newRate New reward rate
     */
    function updateRewardRate(CreatorToken.RewardType rewardType, uint256 newRate) external onlyOwner {
        rewardRates[rewardType] = newRate;
        emit RewardRateUpdated(rewardType, newRate);
    }

    /**
     * @dev Update engagement cooldown period
     * @param newCooldown New cooldown period in seconds
     */
    function updateEngagementCooldown(uint256 newCooldown) external onlyOwner {
        require(newCooldown >= 300, "EngagementReward: Cooldown too short"); // Min 5 minutes
        require(newCooldown <= 86400, "EngagementReward: Cooldown too long"); // Max 24 hours
        engagementCooldown = newCooldown;
    }

    /**
     * @dev Get engagement count for content and type
     * @param contentId Content identifier
     * @param rewardType Type of engagement
     */
    function getEngagementCount(bytes32 contentId, CreatorToken.RewardType rewardType) external view returns (uint256) {
        return engagementCounts[contentId][rewardType];
    }

    /**
     * @dev Check if user has engaged with content
     * @param user User address
     * @param contentId Content identifier
     * @param rewardType Type of engagement
     */
    function hasUserEngaged(
        address user,
        bytes32 contentId,
        CreatorToken.RewardType rewardType
    ) external view returns (bool) {
        return hasEngaged[user][contentId][rewardType];
    }

    /**
     * @dev Get reward rate for engagement type
     * @param rewardType Type of engagement
     */
    function getRewardRate(CreatorToken.RewardType rewardType) external view returns (uint256) {
        return rewardRates[rewardType];
    }

    /**
     * @dev Get total engagement metrics for content
     * @param contentId Content identifier
     */
    function getContentEngagement(bytes32 contentId) external view returns (
        uint256 likes,
        uint256 comments,
        uint256 shares,
        uint256 remixes,
        uint256 totalEngagements
    ) {
        likes = engagementCounts[contentId][CreatorToken.RewardType.LIKE];
        comments = engagementCounts[contentId][CreatorToken.RewardType.COMMENT];
        shares = engagementCounts[contentId][CreatorToken.RewardType.SHARE];
        remixes = engagementCounts[contentId][CreatorToken.RewardType.REMIX];
        totalEngagements = likes + comments + shares + remixes;
    }

    /**
     * @dev Emergency withdraw tokens (only owner)
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(uint256 amount) external onlyOwner {
        creatorToken.transfer(owner(), amount);
    }
}

contracts/RevenueSplitter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Address.sol";

/**
 * @title RevenueSplitter
 * @dev Contract for splitting revenue among multiple recipients based on predefined percentages
 * Supports both ETH and ERC20 token distributions
 */
contract RevenueSplitter is Ownable, ReentrancyGuard {
    using Address for address payable;

    // Events
    event RevenueSplit(
        bytes32 indexed contentId,
        address indexed payer,
        uint256 totalAmount,
        address token
    );

    event SplitUpdated(bytes32 indexed contentId, address[] recipients, uint256[] percentages);

    // Struct for revenue splits
    struct Split {
        address[] recipients;
        uint256[] percentages; // In basis points (100 = 1%)
        uint256 totalPercentage; // Should equal 10000 (100%)
        bool isActive;
    }

    // Mapping of content ID to revenue split configuration
    mapping(bytes32 => Split) public splits;

    // Platform fee recipient and percentage
    address public platformWallet;
    uint256 public platformFee = 500; // 5% in basis points

    constructor(address _platformWallet) Ownable(msg.sender) {
        require(_platformWallet != address(0), "RevenueSplitter: Invalid platform wallet");
        platformWallet = _platformWallet;
    }

    /**
     * @dev Set revenue split for a content piece
     * @param contentId Unique identifier for the content
     * @param recipients Array of recipient addresses
     * @param percentages Array of percentages in basis points
     */
    function setSplit(
        bytes32 contentId,
        address[] calldata recipients,
        uint256[] calldata percentages
    ) external onlyOwner {
        require(recipients.length == percentages.length, "RevenueSplitter: Arrays length mismatch");
        require(recipients.length > 0, "RevenueSplitter: No recipients");
        require(recipients.length <= 10, "RevenueSplitter: Too many recipients");

        uint256 totalPercentage = 0;
        for (uint256 i = 0; i < percentages.length; i++) {
            require(percentages[i] > 0, "RevenueSplitter: Invalid percentage");
            require(recipients[i] != address(0), "RevenueSplitter: Invalid recipient");
            totalPercentage += percentages[i];
        }

        require(totalPercentage == 10000, "RevenueSplitter: Percentages must sum to 100%");

        splits[contentId] = Split({
            recipients: recipients,
            percentages: percentages,
            totalPercentage: totalPercentage,
            isActive: true
        });

        emit SplitUpdated(contentId, recipients, percentages);
    }

    /**
     * @dev Split ETH revenue according to predefined percentages
     * @param contentId Content identifier
     */
    function splitRevenue(bytes32 contentId) external payable nonReentrant {
        Split storage split = splits[contentId];
        require(split.isActive, "RevenueSplitter: Split not active");
        require(msg.value > 0, "RevenueSplitter: No ETH sent");

        uint256 totalAmount = msg.value;
        uint256 platformAmount = (totalAmount * platformFee) / 10000;
        uint256 remainingAmount = totalAmount - platformAmount;

        // Send platform fee
        payable(platformWallet).sendValue(platformAmount);

        // Distribute to recipients
        for (uint256 i = 0; i < split.recipients.length; i++) {
            uint256 recipientAmount = (remainingAmount * split.percentages[i]) / 10000;
            payable(split.recipients[i]).sendValue(recipientAmount);
        }

        emit RevenueSplit(contentId, msg.sender, totalAmount, address(0));
    }

    /**
     * @dev Split ERC20 token revenue according to predefined percentages
     * @param contentId Content identifier
     * @param token ERC20 token address
     * @param amount Amount of tokens to split
     */
    function splitTokenRevenue(
        bytes32 contentId,
        address token,
        uint256 amount
    ) external nonReentrant {
        require(token != address(0), "RevenueSplitter: Invalid token address");
        require(amount > 0, "RevenueSplitter: Invalid amount");

        Split storage split = splits[contentId];
        require(split.isActive, "RevenueSplitter: Split not active");

        // Transfer tokens from sender to this contract
        IERC20(token).transferFrom(msg.sender, address(this), amount);

        uint256 platformAmount = (amount * platformFee) / 10000;
        uint256 remainingAmount = amount - platformAmount;

        // Send platform fee
        IERC20(token).transfer(platformWallet, platformAmount);

        // Distribute to recipients
        for (uint256 i = 0; i < split.recipients.length; i++) {
            uint256 recipientAmount = (remainingAmount * split.percentages[i]) / 10000;
            IERC20(token).transfer(split.recipients[i], recipientAmount);
        }

        emit RevenueSplit(contentId, msg.sender, amount, token);
    }

    /**
     * @dev Get split configuration for content
     * @param contentId Content identifier
     */
    function getSplit(bytes32 contentId) external view returns (
        address[] memory recipients,
        uint256[] memory percentages,
        bool isActive
    ) {
        Split storage split = splits[contentId];
        return (split.recipients, split.percentages, split.isActive);
    }

    /**
     * @dev Update platform fee (only owner)
     * @param newFee New platform fee in basis points
     */
    function setPlatformFee(uint256 newFee) external onlyOwner {
        require(newFee <= 1000, "RevenueSplitter: Fee too high"); // Max 10%
        platformFee = newFee;
    }

    /**
     * @dev Update platform wallet (only owner)
     * @param newWallet New platform wallet address
     */
    function setPlatformWallet(address newWallet) external onlyOwner {
        require(newWallet != address(0), "RevenueSplitter: Invalid wallet");
        platformWallet = newWallet;
    }

    /**
     * @dev Deactivate a split (only owner)
     * @param contentId Content identifier
     */
    function deactivateSplit(bytes32 contentId) external onlyOwner {
        splits[contentId].isActive = false;
    }

    /**
     * @dev Emergency withdraw function (only owner)
     * @param token Token address (address(0) for ETH)
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        if (token == address(0)) {
            payable(owner()).sendValue(amount);
        } else {
            IERC20(token).transfer(owner(), amount);
        }
    }

    // Fallback function to receive ETH
    receive() external payable {}
}

